package primeros_ejercicios;

public class Ejercicio9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String word1 = "hola";
		String word2 = "adios";
		
		if (word1.length() > word2.length()) {
			System.out.println("La palabra " + word1 + " es más larga que la palabra " + word2);
		} else {
			System.out.println("La palabra " + word2 + " es más larga que la palabra " + word1);
		}
	}

}
